class GeoLoc{constructor(){if(!("geolocation"in navigator))throw new Error("Geolocation is not supported by your browser");this.geo=navigator.geolocation}get(){return new Promise(((o,e)=>{this.geo.getCurrentPosition((e=>{o(e)}),(o=>{e(o)}))}))}watch(){return new Promise(((o,e)=>{this.geo.watchPosition((e=>{o(e)}),(o=>{e(o)}),{enableHighAccuracy:1,timeout:1e3,maximumAge:0})}))}clear(){this.geo.clearWatch()}}