/**
 * @name Img2Link
 * @author XHiddenProjects
 * @version 1.0.0
 * @description Convert Images to links
 * @todo For every <img/> use the "href" to make it into a link and "target" to select target redirect
 */
window.addEventListener("load",(()=>{document.querySelectorAll("img.img-linkable").forEach((e=>{const t=document.createElement("a");t.href=e.getAttribute("href")||"",t.target=e.getAttribute("target")||"",t.innerHTML=e.outerHTML,e.replaceWith(t)}))}));
