class URLParse{constructor(r=null){this.url=r||window.location.href}getURL(){return this.url}getProtocol(){return this.url.split(":")[0]}getPath(){return this.url.split("/").slice(3)}getHost(){return this.url.split("/")[2]}getQuery(){return this.url.split("?")[1]}getHash(){return this.url.split("#")[1]}removeQuery(){return this.url.split("?")[0]}removeHash(){return this.url.split("#")[0]}removeQueryHash(){return this.url.split("?")[0].split("#")[0]}removeProtocol(){return this.url.split(":")[1].slice(2)}removeProtocolHost(){return this.url.split("/")[3]}removeProtocolHostQueryHash(){return this.url.split("/")[2]}removeProtocolHostQuery(){return this.url.split("?")[0].split("/")[2]}removeProtocolHostHash(){return this.url.split("#")[0].split("/")[2]}}